// ============================================
// ASSEMBLE SCRIPT GENERATION CONTEXT
// Per Manifesto Section 4.1
// ============================================

const input = $('Webhook Trigger').first().json.body;
const brief = $('Load Creative Brief').first().json;
const brandContext = $('Get Brand Context').first().json.brand_context;

// Parse strategy from brief
let strategy = {};
try {
  strategy = typeof brief.strategy_data === 'string' ? JSON.parse(brief.strategy_data) : brief.strategy_data || {};
} catch (e) {
  strategy = { creative_direction: brief.strategy_data };
}

return [{
  json: {
    campaign_id: input.campaign_id,
    brief_id: input.brief_id,
    brand_id: input.brand_id,
    target_duration: input.target_duration || 30,
    platform: input.platform || 'tiktok',
    execution_id: $execution.id,

    // Strategy from brief
    creative_direction: strategy.creative_direction || '',
    hook_concept: strategy.hook_concept || '',
    target_emotion: strategy.target_emotion || '',
    call_to_action: strategy.call_to_action || '',
    recommended_trend: strategy.recommended_trend || {},

    // Brand context for ToV injection
    brand_name: brandContext?.brand_name || 'Brand',
    brand_tone: brandContext?.tone || 'energetic',
    brand_rules: brandContext?.brand_rules || [],
    negative_constraints: brandContext?.negative_constraints || [],
    positive_examples: brandContext?.positive_examples || [],

    // Critic loop config
    iteration: 0,
    max_iterations: 3,
    target_score: 85
  }
}];

// Parse script and calculate cost
const context = $('Assemble Script Context').first().json;
const response = $input.all()[0].json;

if (response.error) {
  return [{ json: { success: false, error: 'LLM_ERROR', error_details: response.error, campaign_id: context.campaign_id } }];
}

let script = null;
try {
  const content = response.choices[0].message.content;
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  script = jsonMatch ? JSON.parse(jsonMatch[0]) : { hook: content };
} catch (e) {
  script = { hook: response.choices[0].message.content, scenes: [], raw: true };
}

const usage = response.usage || {};
const costUsd = (usage.prompt_tokens * 0.0000025) + (usage.completion_tokens * 0.00001);

return [{
  json: {
    success: true,
    script: script,
    context: context,
    cost_data: {
      provider: 'openai',
      model: 'gpt-4o',
      tokens_in: usage.prompt_tokens || 0,
      tokens_out: usage.completion_tokens || 0,
      cost_usd: costUsd,
      purpose: 'script_generation',
      campaign_id: context.campaign_id,
      execution_id: context.execution_id
    }
  }
}];

// Parse critic response per Manifesto Section 4.1 - Recursive Critic Loop
const scriptData = $('Parse Script & Cost').first().json;
const response = $input.all()[0].json;

let critique = { score: 50 };
try {
  const content = response.choices[0].message.content;
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  critique = jsonMatch ? JSON.parse(jsonMatch[0]) : { score: 50, critique: content };
} catch (e) {
  critique = { score: 50, critique: 'Failed to parse critic response' };
}

const usage = response.usage || {};
const criticCost = (usage.prompt_tokens * 0.0000025) + (usage.completion_tokens * 0.00001);

const context = scriptData.context;
const iteration = context.iteration + 1;
const score = critique.score || 50;
const passedThreshold = score >= context.target_score;
const canRetry = iteration < context.max_iterations;

return [{
  json: {
    script: scriptData.script,
    critique: critique,
    score: score,
    passed: passedThreshold,
    iteration: iteration,
    max_iterations: context.max_iterations,
    should_retry: !passedThreshold && canRetry,
    context: context,
    improved_hook: critique.improved_hook || null,
    critic_cost: {
      provider: 'openai',
      model: 'gpt-4o',
      tokens_in: usage.prompt_tokens || 0,
      tokens_out: usage.completion_tokens || 0,
      cost_usd: criticCost,
      purpose: 'script_critique',
      campaign_id: context.campaign_id,
      execution_id: context.execution_id
    }
  }
}];

// Prepare for retry with critic feedback
const data = $input.all()[0].json;

// Per Manifesto: Use improved_hook if provided
const updatedContext = {
  ...data.context,
  iteration: data.iteration,
  hook_concept: data.improved_hook || data.context.hook_concept,
  previous_critique: data.critique.critique
};

return [{ json: updatedContext }];

// Brand Safety Filter per Manifesto Section 4.4
const data = $input.all()[0].json;
const script = data.script;
const scriptText = JSON.stringify(script).toLowerCase();
const context = data.context;

// Check for brand safety violations
const violations = [];

// Check negative constraints
for (const keyword of (context.negative_constraints || [])) {
  if (scriptText.includes(keyword.toLowerCase())) {
    violations.push(`Contains restricted keyword: ${keyword}`);
  }
}

// Check for profanity/offensive content (basic check)
const profanityList = ['damn', 'hell', 'crap', 'stupid', 'idiot'];
for (const word of profanityList) {
  if (scriptText.includes(word)) {
    violations.push(`Contains potentially offensive word: ${word}`);
  }
}

const isSafe = violations.length === 0;

return [{
  json: {
    is_safe: isSafe,
    violations: violations,
    script: script,
    critique: data.critique,
    score: data.score,
    context: context,
    campaign_id: context.campaign_id
  }
}];

// Prepare script for storage
const data = $input.all()[0].json;

return [{
  json: {
    campaign_id: data.campaign_id,
    brief_id: data.context.brief_id,
    script_content: JSON.stringify(data.script),
    hook: data.script.hook || '',
    scenes: JSON.stringify(data.script.scenes || []),
    critic_score: data.score,
    target_duration: data.context.target_duration,
    platform: data.context.platform,
    status: 'approved'
  }
}];

// Max retries reached but score still low
const data = $input.all()[0].json;

return [{
  json: {
    campaign_id: data.context.campaign_id,
    final_score: data.score,
    iterations: data.iteration,
    script: data.script,
    context: data.context,
    message: 'Script accepted with low score after max retries'
  }
}];
