// ============================================
// ATOMIC LOCK ACQUISITION
// Sub-Workflow: Acquire_Lock
// Per Manifesto Section 2, Dimension 2
// ============================================

const input = $input.all()[0].json;

// Validate required fields
if (!input.table_name) throw new Error('table_name is required');
if (!input.record_id) throw new Error('record_id is required');
if (!input.workflow_id) throw new Error('workflow_id is required');

// Default lock timeout: 10 minutes (per Manifesto)
const lockTimeoutMinutes = input.lock_timeout_minutes || 10;

return [{
  json: {
    table_name: input.table_name,
    record_id: input.record_id,
    id_column: input.id_column || 'id',
    workflow_id: input.workflow_id,
    expected_status: input.expected_status || null,
    target_status: input.target_status || 'processing',
    lock_timeout_minutes: lockTimeoutMinutes
  }
}];

// Build atomic lock query with UPDATE...RETURNING
// Per Manifesto Section 2, Dimension 2
const input = $input.all()[0].json;

// The canonical locking pattern from the Manifesto:
// UPDATE campaigns 
// SET status = 'processing', 
//     locked_by = $workflow_id, 
//     locked_at = NOW() 
// WHERE id = $campaign_id 
//   AND status = 'pending' 
//   AND (locked_by IS NULL OR locked_at < NOW() - INTERVAL '10 minutes')
// RETURNING *;

let whereClause = `${input.id_column} = '${input.record_id}'`;

// Add status check if expected_status is provided
if (input.expected_status) {
  whereClause += ` AND status = '${input.expected_status}'`;
}

// Add stale lock check (zombie protection)
whereClause += ` AND (locked_by IS NULL OR locked_at < NOW() - INTERVAL '${input.lock_timeout_minutes} minutes')`;

const lockQuery = {
  query: `UPDATE ${input.table_name} SET status = $1, locked_by = $2, locked_at = NOW(), updated_at = NOW() WHERE ${whereClause} RETURNING *`,
  params: [input.target_status, input.workflow_id]
};

return [{
  json: {
    ...input,
    lock_query: lockQuery
  }
}];

// Lock successfully acquired
const lockedRecord = $input.all()[0].json[0];
const originalInput = $('Validate Input').first().json;

return [{
  json: {
    success: true,
    lock_acquired: true,
    locked_record: lockedRecord,
    locked_by: originalInput.workflow_id,
    locked_at: new Date().toISOString(),
    table_name: originalInput.table_name,
    record_id: originalInput.record_id,
    message: 'Lock acquired successfully'
  }
}];

// Lock failed - another workflow has the lock or status mismatch
const originalInput = $('Validate Input').first().json;

return [{
  json: {
    success: false,
    lock_acquired: false,
    locked_record: null,
    table_name: originalInput.table_name,
    record_id: originalInput.record_id,
    reason: 'Record already locked by another workflow or status mismatch',
    message: 'Lock not acquired - exit gracefully per Manifesto'
  }
}];
