// ============================================
// LOCK RELEASE
// Sub-Workflow: Release_Lock
// Per Manifesto Section 2, Dimension 2
// ============================================

const input = $input.all()[0].json;

// Validate required fields
if (!input.table_name) throw new Error('table_name is required');
if (!input.record_id) throw new Error('record_id is required');
if (!input.workflow_id) throw new Error('workflow_id is required');
if (!input.final_status) throw new Error('final_status is required');

return [{
  json: {
    table_name: input.table_name,
    record_id: input.record_id,
    id_column: input.id_column || 'id',
    workflow_id: input.workflow_id,
    final_status: input.final_status,
    additional_updates: input.additional_updates || {}
  }
}];

// Build release query - only release if we own the lock
const input = $input.all()[0].json;

// Build SET clause with additional updates
let setClause = `status = '${input.final_status}', locked_by = NULL, locked_at = NULL, updated_at = NOW()`;

for (const [key, value] of Object.entries(input.additional_updates)) {
  if (typeof value === 'string') {
    setClause += `, ${key} = '${value}'`;
  } else if (typeof value === 'object') {
    setClause += `, ${key} = '${JSON.stringify(value)}'::jsonb`;
  } else {
    setClause += `, ${key} = ${value}`;
  }
}

// Only release if we own the lock (safety check)
const whereClause = `${input.id_column} = '${input.record_id}' AND locked_by = '${input.workflow_id}'`;

const releaseQuery = {
  query: `UPDATE ${input.table_name} SET ${setClause} WHERE ${whereClause} RETURNING *`,
  set_clause: setClause,
  where_clause: whereClause
};

return [{
  json: {
    ...input,
    release_query: releaseQuery
  }
}];

// Lock successfully released
const releasedRecord = $input.all()[0].json[0];
const originalInput = $('Validate Input').first().json;

return [{
  json: {
    success: true,
    lock_released: true,
    released_record: releasedRecord,
    final_status: originalInput.final_status,
    table_name: originalInput.table_name,
    record_id: originalInput.record_id,
    message: 'Lock released successfully'
  }
}];

// Lock release failed - we didn't own the lock
const originalInput = $('Validate Input').first().json;

console.warn(`Lock release failed: workflow ${originalInput.workflow_id} did not own lock on ${originalInput.table_name}:${originalInput.record_id}`);

return [{
  json: {
    success: false,
    lock_released: false,
    table_name: originalInput.table_name,
    record_id: originalInput.record_id,
    workflow_id: originalInput.workflow_id,
    reason: 'Lock not owned by this workflow or already released',
    message: 'Lock release failed - lock not owned'
  }
}];
